import { useEffect, useRef, useState } from 'react';
import styles from './wrapp.module.scss';
import { menu } from '../header'
import { Link } from 'react-router-dom'


export default function Wrapp() {


    const sliderRef = useRef<HTMLDivElement>(null)
    const wrappRef = useRef<HTMLDivElement | any>(null)

    const [element, setElement] = useState<HTMLDivElement | null>(null)
    

    useEffect(() => {
        if (!element) {
            [...wrappRef.current!.children].forEach((element) => {
                if(element.firstChild.pathname === window.location.pathname){
                    return setElement(element)
                }
                
            })
        }

        if (element) {

            window.onresize = () => {
                if (sliderRef.current!.style.width === "0px") {
                    sliderRef.current!.style.width = `${element!.offsetWidth}px`
                }
                sliderRef.current!.classList.remove(styles.active)
                sliderRef.current!.style.left = `${element!.offsetLeft}px`
                if (sliderRef.current!.offsetLeft === element!.offsetLeft) {
                    sliderRef.current!.classList.add(styles.active)
                }
            }

            sliderRef.current!.style.left = `${element!.offsetLeft}px`
            sliderRef.current!.style.width = `${element!.offsetWidth}px`

            if (sliderRef.current!.offsetLeft >= wrappRef.current!.children[0].offsetLeft) {
                sliderRef.current!.classList.add(styles.active)
            }
        }
    }, [element])


    return (

        <div ref={wrappRef} className={styles.wrapp}>
            {menu.map((item, index) => (
                <div className="123" key={index} onClick={(e: { target: Element| any }) => {
                    setElement(e.target)
                }}>
                    <Link className={styles.active} to={item.href}>{item.title}</Link>
                </div>
            ))}
            <span ref={sliderRef} className={styles.slider}>
                <div className={styles.position}></div>
            </span>
        </div>

    )
}
