import styles from "yup/yup.module.scss";

import { forwardRef } from "react";

interface Props {
  innerText: string;
  className: string;
  type: string;
  id: string;
  error?: string;
}

function Field(
  { innerText, className, type, error, id, ...props }: Props,
  ref: any
) {
  console.log(error);

  return (
    <div>
      <label htmlFor={id}>{innerText}</label>
      <input ref={ref} type={type} className={className} id={id} {...props} />
      {error && (
        <p className={styles.error}>
          Cửa hàng chỉ mở cửa: <br /> Sáng: 9h00 - 14h30 <br /> Chiều: 17h00 -
          23h00
        </p>
      )}
    </div>
  );
}

export default forwardRef(Field);
