import styles from "components/Login/login.module.scss";
import Field from "yup/Field";
import logo from "layout/Header/Image/laut12.png";
import src from "./backgroundLogin.jpg";
import { useForm, SubmitHandler } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import yup from "yup/yupGlobal";
import clsx from "clsx";

type Inputs = {
  account: string;
};

const schema = yup.object().shape({});

export default function Login() {
  const {
    handleSubmit,
    register,
    watch,
    formState: { errors },
  } = useForm<Inputs>({ resolver: yupResolver(schema) });
  console.log(watch());

  return (
    <div style={{ backgroundImage: `url(${src})` }} className={styles.wrapp}>
      <div className={styles.login}>
        <div className={styles.container}>
          <div className={styles.main}>
            <div
              style={{ backgroundImage: `url(${logo})` }}
              className={styles.imageShow}
            >
              <img className={styles.image} src={logo} />
            </div>
            <div className={styles.title}>Đăng nhập</div>
            <div className={styles.wrappInput}>
              <Field
                label={false}
                innerText="Tài khoản"
                className={styles.input}
                type="text"
                id="account"
                placeholder="Username"
                {...register("account")}
              />
              <span
                className={clsx(styles.focusInput, {
                  [`${styles["has-value"]}`]: watch().account,
                })}
                data-placeholder=""
              ></span>
            </div>
            <div className={styles.wrappInput}>
              <Field
                label={false}
                innerText="password"
                className={styles.input}
                type="password"
                id="password"
                placeholder="Password"
              />
              <span className={styles.focusInput} data-placeholder=""></span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
