import styles from "yup/yup.module.scss";
import clsx from "clsx";

import { forwardRef, useEffect, useRef } from "react";

interface Props {
  label?: boolean;
  placeholder?: string;
  innerText: string;
  className: string;
  classNameLogic?: string;
  type: string;
  id: string;
  error?: string;
}

function Field(
  {
    label = true,
    placeholder = "",
    innerText,
    className,
    classNameLogic,
    type,
    error,
    id,
    ...props
  }: Props,
  ref: any
) {
  return (
    <>
      {label && <label htmlFor={id}>{innerText}</label>}
      <input
        ref={ref}
        placeholder={placeholder}
        type={type}
        className={clsx(className, {
          [`${classNameLogic}`]: true,
        })}
        id={id}
        {...props}
      />
      {error && (
        <p
          className={styles.error}
          dangerouslySetInnerHTML={{ __html: error }}
        ></p>
      )}
    </>
  );
}

export default forwardRef(Field);
