import * as yup from "yup";
import { AnyObject, Maybe } from "yup/lib/types";

yup.addMethod<yup.NumberSchema>(yup.number, "numberPeople", function (message) {
  return this.test("numberPeople2", message, function (value) {
    const { path, createError } = this;

    if (!value) {
      return createError({
        path,
        message: message ? message : "Field is required!!",
      });
    }

    return true;
  });
});

declare module "yup" {
  /* interface StringSchema<
    TType extends Maybe<string> = string | undefined,
    TContext extends AnyObject = AnyObject,
    TOut extends TType = TType
  > extends yup.BaseSchema<TType, TContext, TOut> {
    emptyAsUndefined(a: any): StringSchema<TType, TContext>;
  } */

  interface NumberSchema<
    TType extends Maybe<number> = number | undefined,
    TContext extends AnyObject = AnyObject,
    TOut extends TType = TType
  > extends yup.BaseSchema<TType, TContext, TOut> {
    numberPeople(message: string): NumberSchema<TType, TContext>;
  }
}

export default yup;
