import styles from "yup/yup.module.scss";

import { forwardRef, useRef } from "react";

interface Props {
  innerText: string;
  className: string;
  type: string;
  id: string;
  error?: string;
}

function Field(
  { innerText, className, type, error, id, ...props }: Props,
  ref: any
) {
  console.log(typeof error);

  const refDiv: React.LegacyRef<HTMLDivElement> = useRef(null);

  if (error) {
    const errorElement = document.createElement("p");
    errorElement.innerHTML = error;
    errorElement.classList.add(styles.error);
    refDiv.current!.appendChild(errorElement);
  }

  return (
    <div ref={refDiv}>
      <label htmlFor={id}>{innerText}</label>
      <input ref={ref} type={type} className={className} id={id} {...props} />
    </div>
  );
}

export default forwardRef(Field);
