import * as yup from "yup";

function custom(message: string) {
  return this.test("should first");
}

yup.addMethod(yup.mixed, "custom", function (message: string) {
  console.log(this);

  return this.test("custom", message, function (message: string) {
    return true;
  });
});

export default yup;
