import styles from "./book.module.scss";
import { useForm, SubmitHandler } from "react-hook-form";
import yup from "../../yup/yupGlobal";
import { yupResolver } from "@hookform/resolvers/yup";

type Inputs = {
  numberPeople: number;
  day: string;
};

const schema = yup.object().shape({
  numberPeople: yup
    .string()
    .matches(/^[1-9][0-9]*$/, "Số người đi ít nhất phải là 1 người")
    .required("Số người đi không được để trống"),
  day: yup.string().required("Ngày đặt bàn không được để trống!!"),
});

export default function Book() {
  const {
    handleSubmit,
    register,
    watch,
    formState: { errors },
  } = useForm<Inputs>({ resolver: yupResolver(schema) });

  const onSubmit: SubmitHandler<Inputs> = (data) => {
    console.log(data);
  };

  console.log(watch("day"));
  console.log(errors);

  return (
    <div className={styles.container}>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className={styles.content}>
          <div className={styles.main}>
            <div className={styles.title}>Đặt Bàn</div>
            <div className={styles.order}>
              <div className={styles.column}></div>
              <div className={styles.column}>
                <div>
                  <label htmlFor="numberPeople">Số người</label>
                  <input
                    className={styles.input}
                    type="text"
                    id="numberPeople"
                    {...register("numberPeople")}
                  />
                </div>
                <div className={styles.schedule}>
                  <div className={styles.day}>
                    <label htmlFor="day">Ngày</label>
                    <input
                      className={styles.input}
                      type="date"
                      id="day"
                      {...register("day")}
                    />
                  </div>
                  <div className={styles.hours}>
                    <label htmlFor="hours">Giờ</label>
                    <input className={styles.input} type="time" id="hours" />
                  </div>
                </div>
                <div className={styles.note}>
                  <label htmlFor="note">Ghi chú</label>
                  <input className={styles.input} type="text" id="note" />
                </div>
              </div>
              <div className={styles.column}>
                <div className={styles.author}>
                  <label htmlFor="author">Họ và Tên người đặt</label>
                  <input className={styles.input} type="text" id="author" />
                </div>
                <div className={styles.phoneNumber}>
                  <label htmlFor="phone">Số điện thoại</label>
                  <input className={styles.input} type="text" id="phone" />
                </div>
                <div className={styles.button}>
                  <button type="submit">Đặt bàn</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
}
